package practice140922;

public class ArrayLargestNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a[]= {9,8,30,68,97};
    int max=0;
    for(int i=0;i<a.length;i++) {
    	if(max<a[i]) {
    		max=a[i]; 
    		}
    	}
    		System.out.println(max);
    	}
} 	
   
    
    	
    
    
	


