package practice200922;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   
		int n=4;
		int s=0;
		for(int i=1;i<=n;i++) {
		       for(int j=1;j<=i;j++) { 
		    	   s+=1;
		    	   System.out.print(s +" " );
		    	 
		       } 
		       System.out.println();
	}

}}
